package com.wandoujia.account.email;public class MailContent {    private String subject;    private String to;    private String from;    private String content;    public String getSubject() {        return subject;    }    public void setSubject(String subject) {        this.subject = subject;    }    public String getTo() {        return to;    }    public void setTo(String to) {        this.to = to;    }    public String getFrom() {        return from;    }    public void setFrom(String from) {        this.from = from;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }}